<script>
import axios from 'axios'
export default {
    name: 'weibo_body_header',
    data(){
        return {
            user_avatar: sessionStorage.getItem('photo')
        }
    },
    created() {
        let theme = localStorage.getItem('theme') ? JSON.parse(localStorage.getItem('theme')): 'light'
        document.documentElement.setAttribute('theme', theme)    
    },
    methods: {
        gohomepage(){
            axios.post("http://127.0.0.1:8088/homepage", {id: sessionStorage.getItem('username')}).then(response=>{
                const params = {
                    id: response.data.id,
                    fans: response.data.fans,
                    followers: response.data.followers,
                    avatar: response.data.photo,
                    banner: response.data.banner,
                }
                this.$router.push({path: '/homepage', query: params});
            })
        },
        checkLogin(){
            let token = sessionStorage.getItem('username')
            if (token) {
                return true
            } else {
                return false
            }
        },
        gologin() {
            this.$router.push({
                path: '/login',
                query: { from: 'weibo_header' }
            })
        },
        changeDark() {
            document.documentElement.setAttribute('theme', 'dark')
            localStorage.setItem('theme', JSON.stringify('dark'))
        },
        changeLight() {
            document.documentElement.setAttribute('theme', 'light')
            localStorage.setItem('theme', JSON.stringify('light'))
        },
        showSetting() {
            this.$router.push('/setting')
        }
    },
}
</script>

